import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import api from '../../services/api';

const StudentGrades = () => {
  const [grades, setGrades] = useState([]);
  const [gpa, setGpa] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { user } = useAuth();

  useEffect(() => {
    fetchGradesAndGpa();
  }, []);

  const fetchGradesAndGpa = async () => {
    try {
      setLoading(true);
      const [gradesResponse, gpaResponse] = await Promise.all([
        api.getStudentGrades(user.id),
        api.calculateGPA(user.id)
      ]);

      console.log('Grades:', gradesResponse.data);
      console.log('GPA:', gpaResponse.data);

      setGrades(gradesResponse.data);
      setGpa(gpaResponse.data.gpa);
    } catch (error) {
      console.error('Error fetching grades and GPA:', error);
      setError('Failed to fetch grades and GPA');
    } finally {
      setLoading(false);
    }
  };

  const formatGPA = (gpaValue) => {
    if (gpaValue === 'N/A' || gpaValue === null) return 'N/A';
    const numericGPA = parseFloat(gpaValue);
    return isNaN(numericGPA) ? 'N/A' : numericGPA.toFixed(2);
  };

  if (loading) {
    return <div className="text-center py-4">Loading grades...</div>;
  }

  if (error) {
    return <div className="text-red-600 text-center py-4">{error}</div>;
  }

  return (
    <div className="space-y-6">
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold mb-4">Your GPA</h2>
        <p className="text-4xl font-bold text-blue-600">
          {formatGPA(gpa)}
        </p>
      </div>

      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold mb-4">Course Grades</h2>
        {grades.length === 0 ? (
          <p className="text-gray-600 text-center">No grades available yet.</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Course
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Grade
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {grades.map((grade, index) => (
                  <tr key={index}>
                    <td className="px-6 py-4 whitespace-nowrap">{grade.Title}</td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      {grade.Grade || 'Not graded'}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default StudentGrades;
